package JAVA;

public class JAVA3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int k42_ii = 0;
		
		k42_ii = 1000 / 3; //333.333333333 
		System.out.printf("#3-1 : %d\n", k42_ii); //333.333333이출력되지않음
		k42_ii = 1000 % 3; // 나머지를 구하는 연산자를 사용한다. 
		System.out.printf("#3-2 : %d\n", k42_ii); //% 나머지 연산자이다! 1이 출력된다.
		}
	}
