package AssignmentA1;

public class ifElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i, j;
		i = 3;
		j = 5;
		
		if (i > j) {
			System.out.println(" i is greater than j");
		} else {
			System.out.println(" j is greater than i or j and i are the same");
		}
	}

}
